// Get the first item from the input
const item = items[0];  // 'items' is the correct n8n context variable

// Safely access the scenes
const scenes = item.json.output?.scenes;

if (!scenes) {
  throw new Error("Scenes not found in input data.");
}

// Return 8 individual items (one per scene)
return Object.entries(scenes).map(([key, value]) => {
  return {
    json: {
      scene: key,
      text: value
    }
  };
});

// Pack all incoming scene items into one item
return [{
  json: {
    scenes: items.map(it => ({
      scene: it.json.scene,
      text: it.json.text
    }))
  }
}];

// Expect: items[0].json.output === string like '{"lines":[ "...", ... ]}'
// Produces: cleanedText (joined with \n) + lines array + basic validity flags.

function wordCount(s){ return s.split(/\s+/).filter(Boolean).length; }

let lines = [];
const raw = items?.[0]?.json?.output;

// If the agent already returned JSON (not stringified), use it
if (Array.isArray(items?.[0]?.json?.lines)) {
  lines = items[0].json.lines;
} else {
  if (typeof raw !== 'string') {
    throw new Error('Cleaner: expected stringified JSON in items[0].json.output');
  }
  try {
    const obj = JSON.parse(raw);
    if (Array.isArray(obj?.lines)) lines = obj.lines;
  } catch (e) {
    throw new Error('Cleaner: could not parse items[0].json.output as JSON');
  }
}

lines = lines.map(s => String(s).trim()).filter(Boolean).slice(0, 8);

// Optional quick validation: ≤14 words and ≤110 chars each
const isValid = lines.length === 8 && lines.every(l => l.length <= 110 && wordCount(l) <= 14);

return [{
  json: {
    lines,
    cleanedText: lines.join('\n'),   // <-- point ElevenLabs to this
    isValid,
    needRetry: !isValid
  }
}];

