// Song Cleaner — parse music_prompt and hand back plain text
// Accepts either items[0].json.music_prompt OR stringified JSON in items[0].json.output

function coercePrompt(root) {
  if (!root) return null;

  // Case A: already parsed JSON with music_prompt
  if (typeof root.music_prompt === 'string') return root.music_prompt;

  // Case B: stringified JSON in .output
  if (typeof root.output === 'string') {
    try {
      const parsed = JSON.parse(root.output);
      if (typeof parsed?.music_prompt === 'string') return parsed.music_prompt;
    } catch (_) {}
  }

  // Case C: nested object in .output.music_prompt
  if (typeof root.output?.music_prompt === 'string') return root.output.music_prompt;

  return null;
}

const prompt = coercePrompt(items?.[0]?.json);
if (prompt == null) {
  throw new Error('Song Cleaner: no music_prompt found (expected json.music_prompt or json.output string with {"music_prompt": ...}).');
}

// Trim and enforce ≤2000 chars
let musicPrompt = String(prompt).trim();
if (musicPrompt.length > 1800) {
  musicPrompt = musicPrompt.slice(0, 1800); // hard trim if slightly over
}

// Basic validation flags
const hasTimeMap =
  /0[\-–]?\s*5/.test(musicPrompt) && /35[\-–]?\s*40/.test(musicPrompt);

return [{
  json: {
    music_prompt: musicPrompt,           // <-- pass this to your music generator
    length: musicPrompt.length,
    isValid: musicPrompt.length > 0 && musicPrompt.length <= 1800 && hasTimeMap,
    needRetry: !(musicPrompt.length > 0 && musicPrompt.length <= 1800 && hasTimeMap)
  }
}];

/**
 * Transfer Images (robust)
 * - Builds the payload for WAN using:
 *   • Cloudinary URL (secure_url) or tempfile fallback
 *   • promptForScene from Prep, or recover from KIE data.param
 *   • stable characterSeed (djb2) + per-scene seed
 * - Ensures WAN seed <= 2147483647
 */

const INT32_MAX = 2147483647;

// Stable character ID you chose earlier:
const CHARACTER_ID = 'frankenstein-karloff-modern-city-v1';

// DJB2 hash -> clamp to INT32_MAX
function djb2(str) {
  let h = 5381;
  for (let i = 0; i < str.length; i++) h = ((h << 5) + h) + str.charCodeAt(i);
  // Mod to WAN range to be safe
  h = (h >>> 0) % INT32_MAX;
  return h;
}

const baseSeed = djb2(CHARACTER_ID);

function recoverPromptFromParam(paramStr) {
  // param is a stringified JSON like:
  // {"model":"google/nano-banana-edit","input":{"prompt":"...","image_urls":[...]}}
  if (!paramStr || typeof paramStr !== 'string') return '';
  try {
    const obj = JSON.parse(paramStr);
    return obj?.input?.prompt ? String(obj.input.prompt) : '';
  } catch {
    return '';
  }
}

return items.map((it, idx) => {
  const j = it.json;

  // 1) Cloudinary URL
  const imageForVideo =
    j.secure_url || j.url || j.generatedImageUrl || j.imageForVideo || '';

  if (!imageForVideo || !/^https?:\/\//i.test(imageForVideo)) {
    throw new Error('Transfer Images: missing image URL (secure_url/url/generatedImageUrl).');
  }

  // 2) Scene index (from earlier or fallback to position)
  const sceneIndex =
    Number.isInteger(j.scene_index) ? j.scene_index : idx;

  // 3) Prompt: prefer Prep field; otherwise recover from KIE data.param
  let promptForScene = (j.promptForScene || '').trim();
  if (!promptForScene) {
    const paramStr = j?.data?.param || j.param;
    promptForScene = recoverPromptFromParam(paramStr).trim();
  }
  if (!promptForScene) {
    throw new Error('Transfer Images: promptForScene is empty and could not be recovered from data.param.');
  }
  const promptEscaped = promptForScene.replace(/\n/g, '\\n');

  // 4) Seeds: carry through if present; else derive deterministically
  let characterSeed =
    Number.isInteger(j.characterSeed) ? j.characterSeed : baseSeed;
  if (characterSeed > INT32_MAX) characterSeed = characterSeed % INT32_MAX;

  let seedForScene =
    Number.isInteger(j.seedForScene) ? j.seedForScene : (characterSeed + sceneIndex);
  if (seedForScene > INT32_MAX) seedForScene = seedForScene % INT32_MAX;

  // 5) Return the compact payload for WAN
  return {
    json: {
      scene_index: sceneIndex,
      imageForVideo,
      promptForScene,
      promptEscaped,
      characterSeed,
      seed: seedForScene,        // if you want per-scene variation use this in WAN
      seedForScene
    }
  };
});

/**
 * Build identity + scene prompts, attach Cloudinary portrait ref,
 * and create stable seeds (clamped to 2,147,483,647).
 */

const CHARACTER_REF_URL =
  'https://res.cloudinary.com/dfzbwj4q2/image/upload/v1756732737/frank1_yh9xxj.png';

const IDENTITY_PREFIX =
  'Boris Karloff–style Frankenstein (1931): pale stitched skin, flat-top hair, neck bolts, hulking dark coat; dignified, curious; classic-horror tone in a modern world. SAME character across all scenes.';

// Stable seed from a fixed ID
const MAX_SEED = 2147483647;
const idString = 'frankenstein-karloff-modern-city-v1';
function djb2(str) { let h = 5381; for (let i=0;i<str.length;i++) h=((h<<5)+h)+str.charCodeAt(i); return h>>>0; }
const baseSeedRaw = djb2(idString);
const baseSeed = baseSeedRaw % (MAX_SEED + 1);

return items.map((it, idx) => {
  const sceneText = String(it.json.text || '').trim();
  const promptForScene =
`${IDENTITY_PREFIX}
Selfie composition; 9:16 portrait; consistent face likeness and costume; cinematic light; no text, captions, or watermark.
Scene: ${sceneText}`;

  // Per-scene offset (optional); still clamped
  const seedForScene = (baseSeed + idx) % (MAX_SEED + 1);

  return {
    json: {
      ...it.json,
      scene_index: idx,
      characterRef: CHARACTER_REF_URL,
      identityPrefix: IDENTITY_PREFIX,
      promptForScene,
      characterSeed: baseSeed,
      seedForScene
    }
  };
});

for (const it of items) {
  if (!it.json.characterRef) throw new Error('characterRef missing');
  if (!it.json.promptForScene) throw new Error('promptForScene missing');
}
return items;

/**
 * Pick Image URL (robust)
 * - Waits for state=success (throws if fail or not ready)
 * - Tries resultJson/result objects (string or object)
 * - Falls back to regex scan of the whole payload
 * -> writes json.generatedImageUrl for Cloudinary
 */

function toObj(maybeJson) {
  if (maybeJson == null) return {};
  if (typeof maybeJson === 'object') return maybeJson;
  if (typeof maybeJson === 'string') {
    try { return JSON.parse(maybeJson); } catch { return {}; }
  }
  return {};
}

function collectUrls(obj) {
  const urls = new Set();
  const push = (u) => {
    if (typeof u === 'string' && /^https?:\/\//i.test(u)) urls.add(u);
  };

  // Common fields
  push(obj.image_url); push(obj.imageUrl); push(obj.url); push(obj.secure_url);

  // Arrays of objects
  const tryArr = (arr) => {
    if (!Array.isArray(arr)) return;
    for (const it of arr) push(it?.url || it?.image_url || it?.secure_url);
  };
  tryArr(obj.images);
  tryArr(obj.outputs);
  tryArr(obj.result);
  tryArr(obj.results);

  return Array.from(urls);
}

const out = [];

for (const item of items) {
  const j = item.json || {};
  const data = j.data || j;

  // 1) Status
  const state = data.state;
  if (state) {
    if (state === 'fail') {
      const code = data.failCode || '';
      const msg  = data.failMsg  || 'Generation failed';
      throw new Error(`Image task failed: ${code} ${msg}`.trim());
    }
    if (state !== 'success') {
      throw new Error(`Image task not ready (state=${state}). Add/extend Wait or poll until success.`);
    }
  }

  // 2) Try structured places
  let rj = toObj(data.resultJson ?? j.resultJson);
  let rr = toObj(data.result ?? j.result);

  let candidates = [
    ...collectUrls(rj),
    ...collectUrls(rr),
    ...collectUrls(data),
    ...collectUrls(j),
  ];

  // 3) Regex fallback over the whole payload (find direct image URLs)
  if (candidates.length === 0) {
    const big = JSON.stringify({ j, data });
    const re = /(https?:\/\/[^\s"'\\]+?\.(?:png|jpe?g|webp|gif))(?:[^\s"'\\]*)?/ig;
    const matches = big.match(re) || [];
    candidates = matches;
  }

  // Prefer tempfile.aiquickdraw.com if present, else first
  const picked =
    candidates.find(u => /tempfile\.aiquickdraw\.com/i.test(u)) ||
    candidates[0];

  if (!picked) {
    throw new Error('Pick Image URL: no HTTP image URL found in resultJson.');
  }

  out.push({ json: { ...j, generatedImageUrl: picked } });
}

return out;
